		WHERE sqidx=#{sqidx}

    	UPDATE shopping_qna

		SET answer = 'N'
		
		WHERE sqidx=#{sqidx}

UPDATE shopping_qna
SET del_yn = 'Y' ,del_date = CURRENT_TIMESTAMP
WHERE sqidx=#{sqidx}

    	UPDATE shopping_product

		SET score = #{score}
			,review_cnt = #{review_cnt}
		
		WHERE spidx=#{spidx} and del_yn='N';

    	UPDATE shopping_review

		SET content = #{content}
